import { useState } from 'react';
import { Input, Button } from 'antd';
function NewRecipeForm({ onNewRecipe }) {
  const [name, setName] = useState('');
  const [instructions, setInstructions] = useState('');
  const [timeToMake, setTimeToMake] = useState('');
  const [vegetarian, setVegetarian] = useState(false);

  function onSubmit(e) {
    e.preventDefault();
    const new_recipe = {
      name: name,
      instructions: instructions,
      time_to_make: timeToMake,
      vegetarian: vegetarian,
    };
    fetch('/recipes', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(new_recipe),
    })
      .then((res) => res.json())
      .then(onNewRecipe(new_recipe));
  }

  return (
    <form onSubmit={onSubmit}>
      <label htmlFor="recipe_name">Recipe name:</label>
      <Input
        size="small"
        type="text"
        onChange={(e) => setName(e.target.value)}
        id="recipe_name"
      />
      <textarea
        name="instructions"
        onChange={(e) => setInstructions(e.target.value)}
        id="instructions"
        cols="30"
        rows="10"
      ></textarea>
      <label htmlFor="time_to_make">Time to make (in minutes):</label>
      <Input
        size="small"
        type="text"
        id="time_to_make"
        onChange={(e) => setTimeToMake(e.target.value)}
      />
      <label htmlFor="vegetarian">Vegetarian?</label>
      <input
        type="checkbox"
        onChange={(e) => setVegetarian(e.target.checked)}
      />
      <Button type="primary submit">Submit</Button>
    </form>
  );
}
export default NewRecipeForm;
